import React, { useState } from "react";
import { TextInput, Button, View, Text, Image, StyleSheet, Pressable } from 'react-native';

export default function Dispositivo ({ name = 'Dispositivo', imageUri, initialOn = false }){
	const [on, setOn] = useState(initialOn);
	const deviceImage = imageUri || 'https://acdn-us.mitiendanube.com/stores/001/593/734/products/ngs-025-rj-marzo-20191-e7ad1646b181ad357216267077747762-1024-1024.jpg';
	const powerIcon = on
		? 'data:image/png;base64,'
		: 'data:image/png;base64,';

	return (
		<View style={styles.card} accessible accessibilityRole="button" accessibilityLabel={`${name} card`}>
			<Image source={{ uri: deviceImage }} style={styles.deviceImage} />

			<View style={styles.content}>
				<Text style={styles.title}>{name}</Text>
				<Text style={[styles.status, on ? styles.statusOn : styles.statusOff]}>
					{on ? 'Encendido' : 'Apagado'}
				</Text>

				<Pressable
					onPress={() => setOn(prev => !prev)}
					style={({ pressed }) => [
						styles.button,
						on ? styles.buttonOn : styles.buttonOff,
						pressed && styles.buttonPressed
					]}
					accessibilityRole="button"
					accessibilityLabel={on ? 'Apagar dispositivo' : 'Encender dispositivo'}
				>
					<Image source={{ uri: powerIcon }} style={styles.icon} />
					<Text style={styles.buttonText}>{on ? 'Apagar' : 'Encender'}</Text>
				</Pressable>
			</View>
		</View>
	)
}

const styles = StyleSheet.create({
	card: {
		flexDirection: 'row',
		alignItems: 'center',
		backgroundColor: '#fff',
		padding: 12,
		borderRadius: 12,
		marginVertical: 8,
		marginHorizontal: 12,
		shadowColor: '#000',
		shadowOffset: { width: 0, height: 2 },
		shadowOpacity: 0.12,
		shadowRadius: 4,
		elevation: 3,
        width: '30%',
	},
	deviceImage: {
		width: 80,
		height: 80,
		borderRadius: 8,
		resizeMode: 'cover',
		marginRight: 12,
	},
	content: {
		flex: 1,
		justifyContent: 'center',
	},
	title: {
		fontSize: 16,
		fontWeight: '600',
		color: '#222',
		marginBottom: 4,
	},
	status: {
		fontSize: 13,
		marginBottom: 8,
	},
	statusOn: {
		color: '#1e8e3e',
	},
	statusOff: {
		color: '#b22222',
	},
	button: {
		flexDirection: 'row',
		alignItems: 'center',
		paddingVertical: 8,
		paddingHorizontal: 12,
		borderRadius: 8,
		alignSelf: 'flex-start',
	},
	buttonOn: {
		backgroundColor: '#ffefef',
		borderWidth: 1,
		borderColor: '#ff7b7b',
	},
	buttonOff: {
		backgroundColor: '#eff6ff',
		borderWidth: 1,
		borderColor: '#7aa3ff',
	},
	buttonPressed: {
		opacity: 0.8,
	},
	icon: {
		width: 18,
		height: 18,
		marginRight: 8,
		tintColor: undefined, // keep original colors of the icon
	},
	buttonText: {
		fontSize: 14,
		fontWeight: '600',
		color: '#222',
	},
});